(function() {
  var CarrierWaveCropper,
    __bind = function(fn, me){ return function(){ return fn.apply(me, arguments); }; };

  dataConfirmModal.setDefaults({
    title: 'Delete Entry?',
    commit: 'Yes',
    cancel: 'No'
  });

  jQuery(function() {
    return new CarrierWaveCropper();
  });

  CarrierWaveCropper = (function() {
    function CarrierWaveCropper() {
      this.updatePreview = __bind(this.updatePreview, this);
      this.update = __bind(this.update, this);
      $('#user_profile_cropbox').Jcrop({
        aspectRatio: 1,
        setSelect: [0, 0, 200, 200],
        onSelect: this.update,
        onChange: this.update
      });
    }

    CarrierWaveCropper.prototype.update = function(coords) {
      $('#user_profile_crop_x').val(coords.x);
      $('#user_profile_crop_y').val(coords.y);
      $('#user_profile_crop_w').val(coords.w);
      $('#user_profile_crop_h').val(coords.h);
      return this.updatePreview(coords);
    };

    CarrierWaveCropper.prototype.updatePreview = function(coords) {
      return $('#user_profile_previewbox').css({
        width: Math.round(100 / coords.w * $('#user_profile_cropbox').width()) + 'px',
        height: Math.round(100 / coords.h * $('#user_profile_cropbox').height()) + 'px',
        marginLeft: '-' + Math.round(100 / coords.w * coords.x) + 'px',
        marginTop: '-' + Math.round(100 / coords.h * coords.y) + 'px'
      });
    };

    return CarrierWaveCropper;

  })();

}).call(this);
